function pageprocessorPayStubPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.payment_date = $rootScope.pss.PAY_DATE;

    var url = $rootScope.middlewareServerUrl + "/services/student/bannergetearnings/yr=" + $rootScope.selyear + "&payno=" + $rootScope.pss.PAYNO + "&seqno=" + $rootScope.pss.SEQNO +
        "&pay_date=" + $rootScope.pss.PAY_DATE_PARAM + "&pict=" + $rootScope.pss.PICT;
    $rootScope.getAPI(url, function(data) {
        $scope.earnings = data;
        var jobs = [];
        $.each($scope.earnings.getytdearnings, function(i, val) {
            if (val.JOB_DESC.length > 0) {
                var found = false;
                if (jobs || jobs.length > 0) {
                    for (var j = 0; j < jobs.length; j++) {
                        if (val.JOB_DESC == jobs[j]) {
                            found = true;
                        }
                    }
                    if (!found) {
                        jobs.push(val.JOB_DESC);
                    }
                } else {
                    jobs.push(val.JOB_DESC);
                }

            }
        });
        $scope.jobs = jobs;
    });

    $scope.getPosition = function(job) {
        var i = 0;
        var retStr = "";
        if (job && job.length > 0) {
            i = job.indexOf(",");
            if (i >= 0) {
                retStr = job.substr(0, i);
            }
        }
        return retStr;
    }

    $scope.getCourseInfo = function(code,job_desc) {
        var retval = "";
        $.each($scope.earnings.getdmaccadjunctinfo, function(i,val){
            if (val.PHREARN_EARN_CODE == code && val.JOB_DESC == job_desc) {
                retval = retval + " " + val.COURSE;
            }            
        });
        return retval.trim();
    }

    $scope.earnHours = function(code, job_desc) {
        var retval = "";
        $.each($scope.earnings.getcurrentearnings, function(i, val) {
            if (val.PHREARN_EARN_CODE == code && val.JOB_DESC == job_desc) {
                retval = val.HOURS;
            }
        });
        return retval;
    }
    $scope.earnRate = function(code, job_desc) {
        var retval = "";
        $.each($scope.earnings.getcurrentearnings, function(i, val) {
            if (val.PHREARN_EARN_CODE == code && val.JOB_DESC == job_desc) {
                retval = val.RATE;
            }
        });
        return retval;
    }
    $scope.earnAmt = function(code, job_desc) {
        var retval = "";
        $.each($scope.earnings.getcurrentearnings, function(i, val) {
            if (val.PHREARN_EARN_CODE == code && val.JOB_DESC == job_desc) {
                retval = val.AMOUNT;
            }
        });
        return retval;
    }
}

