    var termdropdownURL = "/services/student/bannertermsForClassLookup";
    $rootScope.getAPI($rootScope.middlewareServerUrl + termdropdownURL, function(data) {
        $scope.termdropdown = data.termdropdown;
    });

    $scope.dropdownselect = function(selectedvalue) {
        var otherSearchParamsURL = "/services/student/bannerdmaccAdvClassSearchFilters1/termCode=";
        $rootScope.getAPI($rootScope.middlewareServerUrl + otherSearchParamsURL + $scope.selectedvalue, function(data) {
            $scope.listdata = data;
            var bannerdmaccAdvClassSearchFilters2 = "/services/student/bannerdmaccAdvClassSearchFilters2/termCode=";
            $rootScope.getAPI($rootScope.middlewareServerUrl + bannerdmaccAdvClassSearchFilters2 + $scope.selectedvalue, function(data1) {
                $scope.listdata2 = data1;
            });
        });
    }

    $scope.search = function() {

        if ($scope.selectedSubject == "") {
            if (window.device) {
                navigator.notification.alert("Please select Subject", null, "Look Up Classes", null);
            } else {
                apprise("Please select Subject", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {

                });
            }
            return;
        }
        var searchURL = "/services/student/bannerdmaccClassSearch/";
        /*+ $scope.selectedvalue +
            "&subjectCode=" + $scope.selectedSubject +
            "&courseNumber=" + $scope.courseNumber +
            "&partTermCode=" + $scope.partTermCode +
            "&facultyPidm=" + $scope.selectedInstructor +
            "&courseTitle=" + $scope.courseTitle +
            "&creditFrom=" + $scope.creditFrom +
            "&creditTo=" + $scope.creditTo;*/
        var postdata = "termCode=" + $scope.selectedvalue;
        postdata += "&camp_list_in=" + ($scope.selectedLocation == null ? "" : $scope.selectedLocation);
        postdata += "&subj_code_in=" + $scope.selectedSubject;
        postdata += "&course_in=" + ($scope.courseNumber == null ? "" : $scope.courseNumber);
        postdata += "&title_in=" + ($scope.courseTitle == null ? "" : $scope.courseTitle);
        postdata += "&course_available_in=" + ($scope.courseAvailability == null ? "" : $scope.courseAvailability);
        postdata += "&start_date_in=" + ($scope.startDate == null ? "" : $scope.startDate);
        postdata += "&instmeth_list_in=" + ($scope.selectedInstMethod == null ? "" : $scope.selectedInstMethod);
        postdata += "&credit_from_in=" + ($scope.creditFrom == null ? "" : $scope.creditFrom);
        postdata += "&credit_to_in=" + ($scope.creditTo == null ? "" : $scope.creditTo);
        postdata += "&part_of_term_in=" + ($scope.partofTerm == null ? "" : $scope.partofTerm);
        postdata += "&days_in=" + ($scope.selectedDays == null ? "" : $scope.selectedDays);


        var STHrs = $scope.stHrs;
        var STMin = ($scope.stMins == "") ? "00" : $scope.stMins;
        var STAmPm = $scope.stampm;

        if (STHrs == null || STHrs == "") {
            postdata += "&begin_time_in="
        } else {
            if (STAmPm == "am") {
                var hr = STHrs;
                var min = STMin;

                postdata += "&begin_time_in=" + hr + "" + min;
            } else {
                var hr = STHrs;
                var min = STMin;
                if (hr != '12') {
                    hr = parseInt(STHrs) + 12;
                }
                postdata += "&begin_time_in=" + hr + "" + min;
            }
        }

        var ETHrs = $scope.endHrs;
        var ETMin = ($scope.endMins == "") ? "00" : $scope.endMins;
        var ETAmPm = $scope.endampm;

        if (ETHrs == null || ETHrs == "") {
            postdata += "&end_time_in="
        } else {
            if (ETAmPm == "am") {
                var hr = ETHrs;
                var min = ETMin;

                postdata += "&end_time_in=" + hr + "" + min;
            } else {
                var hr = ETHrs;
                var min = ETMin;
                if (hr != '12') {
                    hr = parseInt(ETHrs) + 12;
                }
                postdata += "&end_time_in=" + hr + "" + min;
            }
        }      
        $rootScope.postAPI($rootScope.middlewareServerUrl + searchURL, postdata, function(data) {
            if (data.result_clob.length == 0) {
                if (window.device) {
                    alert("No record found.");
                } else {
                    apprise("No record found.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {

                    });
                }
            } else {
                $rootScope.classlookup = data.result_clob;
                $location.path("/app/LookupClasses/LookupClassesDetails");
            }
        });
    }
}

