function pageprocessorRegistrationB1Details(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    var type = $routeParams.type;
    var termCode = $routeParams.termCode;
    var subjCode = $routeParams.subjCode;
    $scope.termCode = termCode;
    $scope.loadCurrentClasses = function () {
        var url = $rootScope.middlewareServerUrl + "/services/student/bannerstudentDetailSchedule/termCode=" + termCode;
        $rootScope.getAPI(url, function (data) {
            if (data.scheduleCourseDetail && data.scheduleCourseDetail.length > 0) {
                $scope.scheduleCourseDetail = data.scheduleCourseDetail;
                angular.forEach($scope.scheduleCourseDetail, function (value, key) {
                    if (value.REG_CODE_DESC.indexOf('Drop') >= 0 || value.REG_CODE_DESC.indexOf('Delete') >= 0) {
                        value.DROPPABLE = false;
                        value.DESC_STYLE = {
                            color: "red"
                        }
                    } else {
                        value.DROPPABLE = true;
                        value.DESC_STYLE = {}
                    }
                });
            } else {
                apprise("No current classes could be found for selected term code", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {
                    $rootScope.$apply(function () {
                        $location.path("/app/RegistrationB1/RegistrationB1");
                    });
                });
            }
        });
    }
    if (type == "lookup") {
        $scope.isadd = true;
        var url = $rootScope.middlewareServerUrl + "/services/student/bannerstudentDetailSchedule/termCode=" + termCode +
            "&subjectCode=" + subjCode;
