function pageprocessorEContact(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function (data) {
        $scope.emergencycontactcodes = data;
        $rootScope.countrycodes = data.countrycodes;
        $rootScope.statecodes = data.statecodes;
        $rootScope.relationshipcodes = data.relationshipcodes;
        $scope.getCountry = function (code) {
            var country = "";
            angular.forEach($scope.emergencycontactcodes.countrycodes, function (value, key) {
                if (value.COUNTRY_CODE == code) {
                    country = value.COUNTRY_DESC;
                }
            });
            return country;
        }
        $scope.getState = function (code) {
            var state = "";
            angular.forEach($scope.emergencycontactcodes.statecodes, function (value, key) {
                if (value.STATE_CODE == code) {
                    state = value.STATE_DESC;
                }
            });
            return state;
        }
        $scope.getRelation = function (code) {
            var rel = "";
            angular.forEach($scope.emergencycontactcodes.relationshipcodes, function (value, key) {
                if (value.RELATIONSHIP_CODE == code) {
                    rel = value.RELATIONSHIP_DESC;
                }
            });
            return rel;
        }

    });

    $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/banneremergencycontactfetch", function (data) {
        $scope.emergencycontacts = data.fetch;
        $rootScope.emergencycontacts = data.fetch;
    });

    $scope.deleteCt = function (priority) {

        $rootScope.postAPI($rootScope.middlewareServerUrl + "/services/student/banneremergencycontactdelete", "priority=" + priority,
            function (data) {
                if (data) {
                    apprise("Contact deleted successfully", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function (r) {
                        $rootScope.$apply(function () {
                            $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/banneremergencycontactfetch", function (data) {
                                $scope.emergencycontacts = data.fetch;
                                $rootScope.emergencycontacts = data.fetch;
                            });
                        });
                    });
                }
            });
    }

    $scope.deleteContact = function (priority) {
        var onConfirm = function (buttonIndex) {
            if (buttonIndex == 1) {
                $scope.deleteCt(priority);
            }
        };
        if (window.device) {
            navigator.notification.confirm(
                'Are you sure you want to delete this contact?', // message
                onConfirm, // callback to invoke with index of button pressed
                'Emergency Contact', // title
                ['Yes', 'No'] // buttonLabels
            );
        } else {
            apprise("Are you sure you want to delete this contact?", {
                'verify': true,
                'textYes': "Yes",
                'textNo': "No"
            }, function (r) {
                if (r) {
                    $scope.deleteCt(priority);
                }
            });
        }

    }
}

function pageprocessorEContactDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var index = $routeParams.index
    $scope.btnlabel = "Update Contact";
    if (index == -1) {
        var priority = $rootScope.emergencycontacts.length + 1;
        $scope.emergencycontact = {
            PRIORITY: priority,
            LAST_NAME: "",
            FIRST_NAME: "",
            MIDDLE_NAME: "",
            HOUSE_NUMBER: "",
            STREET_LINE1: "",
            STREET_LINE2: "",
            STREET_LINE3: "",
            STREET_LINE4: "",
            CITY: "",
            STATE_CODE: "",
            COUNTRY_CODE: "",
            ZIP: "",
            PHONE_AREA: "",
            PHONE_NUMBER: "",
            PHONE_EXTN: "",
            PHONE_COUNTRY_CODE: "",
            RELATIONSHIP_CODE: ""
        };
        $scope.btnlabel = "Add Contact";
    } else {
        var selectedContact = $rootScope.emergencycontacts[index];
        $scope.emergencycontact = selectedContact;
    }

    $scope.showalert = function(message, title) {
        if (window.device) {
            navigator.notification.alert(message, null, title, null);
        } else {
            apprise(message, {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {});
        }
    }
    $scope.validateRequired = function() {
        if ($scope.emergencycontact.RELATIONSHIP_CODE == "") {
            $scope.showalert("Please select Relationship", "Emergency Contact");
            return false;
        }
        if ($scope.emergencycontact.FIRST_NAME.trim() == "") {
            $scope.showalert("Please enter First name", "Emergency Contact");
            return false;
        }
        if ($scope.emergencycontact.LAST_NAME.trim() == "") {
            $scope.showalert("Please enter Last name", "Emergency Contact");
            return false;
        }
        if ($scope.emergencycontact.COUNTRY_CODE.trim() != "") {
            var city = $scope.emergencycontact.CITY == null ? "" : $scope.emergencycontact.CITY;
            if (city == "" || city.trim() == "") {
                $scope.showalert("Please enter City", "Emergency Contact");
                return false;
            }

            if ($scope.emergencycontact.STREET_LINE1 == "") {
                $scope.showalert("Please enter Address line 1", "Emergency Contact");
                return false;
            }
        }
        return true
    }
    $scope.updateContact = function() {
        if ($scope.validateRequired() == false) {
            return;
        }
        var postdata = "priority=" + $scope.emergencycontact.PRIORITY + "&";
        postdata += "oldPriority=" + $scope.emergencycontact.PRIORITY + "&";
        postdata += "lastName=" + ($scope.emergencycontact.LAST_NAME == null ? "" : $scope.emergencycontact.LAST_NAME) + "&";
        postdata += "firstName=" + ($scope.emergencycontact.FIRST_NAME == null ? "" : $scope.emergencycontact.FIRST_NAME) + "&";
        postdata += "middleName=" + $scope.emergencycontact.MIDDLE_NAME + "&";
        postdata += "surnamePrefix=" + $scope.emergencycontact.SURNAME_PREFIX + "&";
        postdata += "houseNumber=" + ($scope.emergencycontact.HOUSE_NUMBER == null ? "" : $scope.emergencycontact.HOUSE_NUMBER) + "&";
        postdata += "streetLine1=" + ($scope.emergencycontact.STREET_LINE1 == null ? "" : $scope.emergencycontact.STREET_LINE1) + "&";
        postdata += "streetLine2=" + ($scope.emergencycontact.STREET_LINE2 == null ? "" : $scope.emergencycontact.STREET_LINE2) + "&";
        postdata += "streetLine3=" + ($scope.emergencycontact.STREET_LINE3 == null ? "" : $scope.emergencycontact.STREET_LINE3) + "&";
        postdata += "streetLine4=" + ($scope.emergencycontact.STREET_LINE4 == null ? "" : $scope.emergencycontact.STREET_LINE4) + "&";
        postdata += "city=" + ($scope.emergencycontact.CITY == null ? "" : $scope.emergencycontact.CITY) + "&";
        postdata += "stateCode=" + $scope.emergencycontact.STATE_CODE + "&";
        postdata += "countryCode=" + $scope.emergencycontact.COUNTRY_CODE + "&";
        postdata += "zipCode=" + ($scope.emergencycontact.ZIP == null ? "" : $scope.emergencycontact.ZIP) + "&";
        postdata += "phoneArea=" + ($scope.emergencycontact.PHONE_AREA == null ? "" : $scope.emergencycontact.PHONE_AREA) + "&";
        postdata += "phoneNumber=" + ($scope.emergencycontact.PHONE_NUMBER == null ? "" : $scope.emergencycontact.PHONE_NUMBER) + "&";
        postdata += "phoneExtn=" + ($scope.emergencycontact.PHONE_EXTN == null ? "" : $scope.emergencycontact.PHONE_EXTN) + "&";
        postdata += "phoneCountryCode=" + ($scope.emergencycontact.PHONE_COUNTRY_CODE == null ? "" : $scope.emergencycontact.PHONE_COUNTRY_CODE) + "&";
        postdata += "reltCode=" + $scope.emergencycontact.RELATIONSHIP_CODE;
        $rootScope.postAPI($rootScope.middlewareServerUrl + pagedef.endpoint, postdata, function(data) {
            if (data) {
                apprise("Contact saved successfully", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {
                    $rootScope.$apply(function() {
                        $location.path("/app/EContact/EContact");
                    });
                });
            }
        });
    }
}

function pageprocessorAddUpdateB1Details(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var curDate = new Date();
    var months = ["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"];
    var formattedCurDate = (curDate.getDate() < 10 ? '0' + curDate.getDate() : '' + curDate.getDate()) + "-" + months[curDate.getMonth()] + "-" + curDate.getFullYear();
    $scope.updateContext = $routeParams.flag;

    $scope.dropdownselect = function() {
        var selectedAddrType = $scope.address.ADDRESS_TYPE_CODE;
        if (selectedAddrType == "" || $scope.add == false ) {   
            return;
        }
        var found = false;
        $.each($rootScope.addresses.addresses, function(i, val) {
            if (val.ADDRESS_TYPE_CODE == selectedAddrType) {
                found = true;
            }
        });

        if (found) {
            $scope.address.ADDRESS_TYPE_CODE = "";
            $scope.alert("An address exists for this type. Please select a different address type or go back and edit the existing one.", "Address Exists");
        }
    }
    $scope.deleteAdditional = function(index) {
        $scope.address.additionalphones.pop(index);       

    }
    $scope.addAdditional = function() {
        $scope.address.additionalphones.push({
            "PHONE_SEQ": "",
            "PHONE_CODE": "",
            "COUNTRY_CODE": "",
            "AREA_CODE": "",
            "PHONE_NUMBER": "",
            "EXTENSION": "",
            "INTERNATIONAL_ACCESS": "",
            "UNLISTED_IND": "",
            "visible": true,
            "allowdelete" : true
        });
    }
    var index = $routeParams.index;
    var cindex = $routeParams.cindex;
    if (cindex) {
        $scope.title = "Edit Contact Number";
        $scope.add = false;
        $scope.address = $rootScope.addresses.addresses[$routeParams.index];
        if (cindex != -1) {
            $scope.address.additionalphones[cindex].visible = true;
        } else {
            $scope.addAdditional();
        }

    } else if (index == -1) {

        $scope.title = "Add New Address";
        $scope.add = true;
        $scope.address = {
            "ADDRESS_TYPE_CODE": "",
            "HOUSE_NUMBER": "",
            "STREET_LINE1": "",
            "STREET_LINE2": "",
            "STREET_LINE3": "",
            "STREET_LINE4": "",
            "CITY": "",
            "STATE_CODE": "",
            "ZIP": "",
            "COUNTY_CODE": null,
            "NATION_CODE": "",
            "FROM_DATE": formattedCurDate,
            "TO_DATE": "",
            primaryphone: {
                "COUNTRY_CODE": "",
                "AREA_CODE": "",
                "PHONE_NUMBER": "",
                "EXTENSION": "",
                "INTERNATIONAL_ACCESS": "",
                "UNLISTED_IND": ""
            },
            additionalphones: []
        }

    } else {
        $scope.title = "Edit Address";
        $scope.add = true;
        $scope.editaddress = true;
        $scope.address = $rootScope.addresses.addresses[$routeParams.index];
        $scope.address.FROM_DATE = formattedCurDate;
        $scope.address.TO_DATE = "";
        angular.forEach($scope.address.additionalphones, function(value, key) {
            value.visible = true;
        });
    }
    $scope.alert = function(message, title) {
        if (window.device) {
            navigator.notification.alert(message, null, title, null);
        } else {
            apprise(message, {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {});
        }
    }

    $scope.validateAddress = function() {
        if ($scope.address.ADDRESS_TYPE_CODE == "") {
           $scope.alert("Address Type not selected.","Address");
           return;
        }        
        //Validate city
        var city = $scope.address.CITY == null ? "" : $scope.address.CITY;
        /*if (!(city.search(/[^a-z. A-Z. ]+/) === -1)) {
            $scope.alert("Only alphabets and periods are allowed in City", "Address");
            return false;
        }*/
        if (city == "" || city.trim() == "") {
            $scope.alert("Please enter City", "Address");
            return false;
        }

        if ($scope.address.STREET_LINE1 == "") {
            $scope.alert("Please enter Address line 1", "Address");
            return false;
        }
        var zip = $scope.address.ZIP == null ? "" : $scope.address.ZIP.trim();
        if ($scope.address.NATION_CODE == "" && zip == "" && $scope.address.STATE_CODE == "") {
            $scope.alert("State and Zip or Nation is required.", "Address");
            return false;
        }
        if (zip == "" && $scope.address.STATE_CODE != "") {
            $scope.alert("State and Zip or Nation is required.", "Address");
            return false;
        }
        if ($scope.address.NATION_CODE == "zz" && zip == "" && $scope.address.STATE_CODE == "") {
            $scope.alert("State and Zip is required.", "Address");
            return false;
        }
        var phone = $scope.address.primaryphone.PHONE_NUMBER == null ? "" : $scope.address.primaryphone.PHONE_NUMBER.trim();
        if (phone == "") {
            $scope.alert("Phone number is required.", "Address");
            return false;
        }
        //validation for additional tel contact
        var valerrorcode = false;
        var valerrornum  = false;
        angular.forEach($scope.address.additionalphones, function(value, key) {
            if (value.PHONE_CODE == "") {
                valerrorcode = true;
                return false;
            }
            if (value.PHONE_NUMBER == "") {
                valerrornum = true;
                return false;
            }

        });
        if (valerrorcode) {
            $scope.alert("Telehone Type is required.","Address");
            return false;
        } else if (valerrornum) {
            $scope.alert("Phone number is required.","Address");
            return false;
        }
        return true
    }

    $scope.addAddress = function() {        
        if ($scope.validateAddress() == false) {
            return;
        }

        var postdata = "";
        if (!$scope.add || $scope.editaddress == true) {
            postdata += "seqno=" + $scope.address.ADDRESS_SEQ + "&";
        }
        postdata += "atypcode=" + $scope.address.ADDRESS_TYPE_CODE + "&";
        postdata += "fromdate=" + ($scope.address.FROM_DATE == null ? "" : $scope.address.FROM_DATE) + "&";
        postdata += "todate=" + ($scope.address.TO_DATE == null ? "" : $scope.address.TO_DATE) + "&";
        postdata += "housenumber=" + ($scope.address.HOUSE_NUMBER == null ? "" : $scope.address.HOUSE_NUMBER) + "&";
